/**
 * 平滑滚动页面到某元素
 * <a href="body" id="toBody" duration="slow">to Top</a>
 *
 * duration = slow | fast | millisecond
 *
 *
 * $("#toBody").toAnywhere(int duration);
 * 
 *
 * @author wuyaohui
 * https://github.com/wuyaohui/toAnywhere
 * email: charsky.wu@gmail.com
 * 2011/11/23 
 */
(function(a){a.fn.toAnywhere=function(b){if(!(b=parseInt(b))){b="slow"}a(this).click(function(){var d=a(this);var c=parseInt(d.attr("duration"));a("body, html").animate({scrollTop:a(d.attr("href")).offset().top+"px"},c?c:b);return false})}})(jQuery);
